00235                                     }}
00236                                 }}
00237                             }}
00238                         }}
00239                     }}
00240                     totalCount
00241                 }}
00242             }}";
00243 
00244             var client = new GraphQLClient($"{DojoEntitiesStorage.worldManagerData.toriiUrl}/graphql");
