00249                 Query = queryString,
00250             };
00251 
00252             var response = await client.Send<dynamic>(request);
00253             var jsonString = Newtonsoft.Json.JsonConvert.SerializeObject(response, Newtonsoft.Json.Formatting.Indented);
00254             JObject jsonObj = JObject.Parse(jsonString);
00255 
00256             int totalCount = (int)jsonObj["Data"]["blobArenaArcadeBlobertModels"]["totalCount"];
00257 
00258             // Define the expected custom background IDs
00259             var expectedBackgrounds = new HashSet<int> { 51, 52, 53, 54, 55, 56 };
00260 
00261             // If totalCount is 6, all custom IDs are present
00262             if (totalCount == 6)
00263             {
00264                 return new List<int>(); // No missing IDs
00265             }
00266 
00267             // If count is not 6, retrieve the custom IDs and find the missing ones
00268             List<int> existingCustomIds = new List<int>();
00269             var edges = jsonObj["Data"]["blobArenaArcadeBlobertModels"]["edges"];
00270 
00271             foreach (var edge in edges)
00272             {
00273                 var models = edge["node"]["entity"]["models"];
00274 
00275                 foreach (var model in models)
00276                 {
00277                     if ((string)model["__typename"] == "blob_arena_ArcadeBlobert" && (bool)model["is_custom"])
00278                     {
00279                         int customId = (int)model["custom_id"];
00280                         existingCustomIds.Add(customId);
00281                     }
00282                 }
00283             }
00284 
00285             // Determine missing IDs by comparing existing custom IDs to expectedBackgrounds
00286             var missingBackgrounds = expectedBackgrounds.Except(existingCustomIds).ToList();
00287 
00288             return missingBackgrounds;
00289         }
00290         catch (Exception e)
00291         {
00292             Debug.Log(e.Message);
00293             Debug.Log("The query for the blob failed");
00294             return new List<int> { 0, 0, 0, 0, 0, 0, 0 };
00295         }
