00303     public static async Task<(bool allowedToMint, int gamesLeft)> CheckIfAbleToMintMickey(string senderAddress)
00304     {
00305         const int maxEndedThreshold = 3;
00306         int endedCount = 0;
00307 
00308         try
00309         {
00310             // Query for games where the user is the sender
00311             endedCount += await GetEndedCountForRole("sender", senderAddress);
00312             if (endedCount > maxEndedThreshold)
00313             {
00314                 Debug.Log($"Threshold exceeded with sender games count: {endedCount}");
00315                 return (true, 0); // Exit early if count exceeds threshold
00316             }
00317 
00318             // Query for games where the user is the receiver
00319             endedCount += await GetEndedCountForRole("receiver", senderAddress);
00320             if (endedCount > maxEndedThreshold)
00321             {
00322                 Debug.Log($"Threshold exceeded with combined count: {endedCount}");
00323                 return (true, 0); // Exit early if count exceeds threshold
00324             }
00325 
00326             Debug.Log($"Total ended count: {endedCount}");
00327             return (false, endedCount); // Return false if count <= maxEndedThreshold
00328         }
00329         catch (Exception e)
00330         {
00331             Debug.LogError($"Query failed: {e.Message}");
00332             return (false, endedCount);
00333         }
00334     }
