00342     private static async Task<int> GetEndedCountForRole(string role, string address)
00343     {
00344         int endedCount = 0;
00345 
00346         var queryString = @$"
00347         query {{
00348             blobArenaPvPChallengeInviteModels(
00349                 where: {{ {role}: ""{address}"" }},
00350                 last: 400
00351             ) {{
00352                 edges {{
00353                     node {{
00354                         entity {{
00355                             models {{
00356                                 __typename
00357                                 ... on blob_arena_CombatState {{
00358                                     phase {{
00359                                         Ended
00360                                         option
00361                                     }}
00362                                 }}
00363                             }}
00364                         }}
00365                     }}
00366                 }}
00367             }}
00368         }}";
00369 
00370         var client = new GraphQLClient($"{DojoEntitiesStorage.worldManagerData.toriiUrl}/graphql");
00371         var request = new Request
00372         {
00373             Query = queryString,
00374         };
00375 
00376         var response = await client.Send<dynamic>(request);
00377 
00378         // Parse the response to JSON
00379         var jsonString = Newtonsoft.Json.JsonConvert.SerializeObject(response, Newtonsoft.Json.Formatting.Indented);
00380         JObject jsonObj = JObject.Parse(jsonString);
00381 
00382         var edges = jsonObj["Data"]["blobArenaPvPChallengeInviteModels"]["edges"];
00383 
00384         foreach (var edge in edges)
00385         {
00386             var models = edge["node"]["entity"]["models"];
00387             foreach (var model in models)
00388             {
00389                 if ((string)model["__typename"] == "blob_arena_CombatState" &&
00390                     (string)model["phase"]["option"] == "Ended")
00391                 {
00392                     endedCount++;
00393                 }
00394             }
00395         }
00396 
00397         Debug.Log($"{role} query ended count: {endedCount}");
00398         return endedCount;
