00188     public static async Task<List<int>> GetMissingCharacters(FieldElement address)
00189     {
00190         try
00191         {
00192             // Updated query with where clause for owner and is_custom: true
00193             var queryString = $@"
00194             query {{
00195                 blobArenaArcadeBlobertModels (where: {{ is_custom: true, owner: ""{address.Hex()}"" }}) {{
00196                     edges {{
00197                         node {{
00198                             entity {{
00199                                 keys
00200                                 models {{
00201                                     __typename
00202                                     ... on blob_arena_ArcadeBlobert {{
00203                                         seed {{
00204                                             background
00205                                             mask
00206                                             armour
00207                                             jewelry
00208                                             weapon
00209                                         }}
00210                                         token_id
00211                                         custom_id
00212                                         is_custom
00213                                     }}
00214                                 }}
00215                             }}
00216                         }}
00217                     }}
00218                     totalCount
00219                 }}
00220             }}";
00221 
00222             var client = new GraphQLClient($"{DojoEntitiesStorage.worldManagerData.toriiUrl}/graphql");
00223             //var client = new GraphQLClient($"https://api.cartridge.gg/x/blob-arena-alpha-twelve/torii/graphql");
00224 
00225             var request = new Request
00226             {
00227                 Query = queryString,
00228             };
00229 
00230             var response = await client.Send<dynamic>(request);
00231             var jsonString = Newtonsoft.Json.JsonConvert.SerializeObject(response, Newtonsoft.Json.Formatting.Indented);
00232             JObject jsonObj = JObject.Parse(jsonString);
00233 
00234             int totalCount = (int)jsonObj["Data"]["blobArenaArcadeBlobertModels"]["totalCount"];
00235 
00236             // Define the expected custom background IDs
00237             var expectedBackgrounds = new HashSet<int> { 51, 52, 53, 54, 55, 56 };
00238 
00239             // If totalCount is 6, all custom IDs are present
00240             if (totalCount == 6)
00241             {
00242                 return new List<int>(); // No missing IDs
00243             }
00244 
00245             // If count is not 6, retrieve the custom IDs and find the missing ones
00246             List<int> existingCustomIds = new List<int>();
00247             var edges = jsonObj["Data"]["blobArenaArcadeBlobertModels"]["edges"];
00248 
00249             foreach (var edge in edges)
00250             {
00251                 var models = edge["node"]["entity"]["models"];
00252 
00253                 foreach (var model in models)
00254                 {
00255                     if ((string)model["__typename"] == "blob_arena_ArcadeBlobert" && (bool)model["is_custom"])
00256                     {
00257                         int customId = (int)model["custom_id"];
00258                         existingCustomIds.Add(customId);
00259                     }
00260                 }
00261             }
00262 
00263             // Determine missing IDs by comparing existing custom IDs to expectedBackgrounds
00264             var missingBackgrounds = expectedBackgrounds.Except(existingCustomIds).ToList();
00265 
00266             return missingBackgrounds;
00267         }
00268         catch (Exception e)
00269         {
00270             Debug.Log(e.Message);
00271             Debug.Log("The query for the blob failed");
00272             return new List<int> { 0, 0, 0, 0, 0, 0, 0 };
00273         }
00274     }
