00278     public static async Task<(bool allowedToMint, int gamesLeft)> CheckIfAbleToMintMickey(string senderAddress)
00279     {
00280         const int maxEndedThreshold = 3;
00281         int endedCount = 0;
00282 
00283         try
00284         {
00285             // Query for games where the user is the sender
00286             endedCount += await GetEndedCountForRole("sender", senderAddress);
00287             if (endedCount > maxEndedThreshold)
00288             {
00289                 Debug.Log($"Threshold exceeded with sender games count: {endedCount}");
00290                 return (true, 0); // Exit early if count exceeds threshold
00291             }
00292 
00293             // Query for games where the user is the receiver
00294             endedCount += await GetEndedCountForRole("receiver", senderAddress);
00295             if (endedCount > maxEndedThreshold)
00296             {
00297                 Debug.Log($"Threshold exceeded with combined count: {endedCount}");
00298                 return (true, 0); // Exit early if count exceeds threshold
00299             }
00300 
00301             Debug.Log($"Total ended count: {endedCount}");
00302             return (false, endedCount); // Return false if count <= maxEndedThreshold
00303         }
00304         catch (Exception e)
00305         {
00306             Debug.LogError($"Query failed: {e.Message}");
00307             return (false, endedCount);
00308         }
00309     }
