00311     private static async Task<int> GetEndedCountForRole(string role, string address)
00312     {
00313         int endedCount = 0;
00314 
00315         var queryString = @$"
00316         query {{
00317             blobArenaPvPChallengeInviteModels(
00318                 where: {{ {role}: ""{address}"" }},
00319                 last: 400
00320             ) {{
00321                 edges {{
00322                     node {{
00323                         entity {{
00324                             models {{
00325                                 __typename
00326                                 ... on blob_arena_CombatState {{
00327                                     phase {{
00328                                         Ended
00329                                         option
00330                                     }}
00331                                 }}
00332                             }}
00333                         }}
00334                     }}
00335                 }}
00336             }}
00337         }}";
00338 
00339         var client = new GraphQLClient($"{DojoEntitiesStorage.worldManagerData.toriiUrl}/graphql");
00340         var request = new Request
00341         {
00342             Query = queryString,
00343         };
00344 
00345         var response = await client.Send<dynamic>(request);
00346 
00347         // Parse the response to JSON
00348         var jsonString = Newtonsoft.Json.JsonConvert.SerializeObject(response, Newtonsoft.Json.Formatting.Indented);
00349         JObject jsonObj = JObject.Parse(jsonString);
00350 
00351         var edges = jsonObj["Data"]["blobArenaPvPChallengeInviteModels"]["edges"];
00352 
00353         foreach (var edge in edges)
00354         {
00355             var models = edge["node"]["entity"]["models"];
00356             foreach (var model in models)
00357             {
00358                 if ((string)model["__typename"] == "blob_arena_CombatState" &&
00359                     (string)model["phase"]["option"] == "Ended")
00360                 {
00361                     endedCount++;
00362                 }
00363             }
00364         }
00365 
00366         Debug.Log($"{role} query ended count: {endedCount}");
00367         return endedCount;
00368     }
