00110 
00111             // Retrieve the attack name or use the enum name if not an attack type
00112             var attackName = IsAttackType(animationState)
00113                 ? GetAttackNameForFighter(fighterId, animationState) // Use custom name for attack types
00114                 : animationState.ToString();
00115 
00116             var sprites = animationData.GetSpritesByAnimName(attackName.ToLower());
00117 
00118             if (sprites != null && sprites.Length != 0)
00119                 return new CharacterAnimationManager.TextureFunctionInput(
00120                     sprites.ToList(),
00121                     sprites.Length
00122                 );
00123 
00124             Debug.LogError($"No sprites found for fighter {fighterId} with state {animationState}");
00125             return default;
