00245         public static string CheckAttackName(string attackName, int fighterId)
00246         {
00247             HashSet<string> attackNamesWithMultiples = new HashSet<string>
00248                 {
00249                     "Sword Slash",
00250                     "Shield Punch",
00251                     "Grind"
