00082     public static CharacterAnimationManager.TextureFunctionInput GetTextureFunctionInput(int fighterId, AmmaAnimationController.AMMAAnimationState animationState)
00083     {
00084         var animationData = GetFighterScriptableObject(fighterId);
00085 
00086         // Retrieve the attack name or use the enum name if not an attack type
00087         var attackName = IsAttackType(animationState)
00088             ? GetAttackNameForFighter(fighterId, animationState) // Use custom name for attack types
00089             : animationState.ToString();
00090 
00091         var sprites = animationData.GetSpritesByAnimName(attackName.ToLower());
00092 
00093         if (sprites != null && sprites.Length != 0)
00094             return new CharacterAnimationManager.TextureFunctionInput(
00095                 sprites.ToList(),
00096                 sprites.Length
00097             );
00098 
00099         Debug.LogError($"No sprites found for fighter {fighterId} with state {animationState}");
00100         return default;
00101     }
