00250     public static List<string> GetAttackOutcomeMessages(Effect[] outcomes, bool isUser)
00251     {
00252         List<string> messages = new List<string>();
00253 
00254         foreach (var outcome in outcomes)
00255         {
00256             string target = outcome.target is Target.Player ? "you" : "the opponent";
00257             string recipient = isUser ? target : (target == "you" ? "the opponent" : "you");
00258 
00259             if (outcome.affect is Affect.Damage damage)
00260             {
00261                 messages.Add($"{recipient} have received {damage.value.power} damage");
00262             }
00263             else if (outcome.affect is Affect.Health health)
00264             {
00265                 messages.Add($"{recipient} lost {Math.Abs(health.value)} health in return");
00266             }
00267             else if (outcome.affect is Affect.Stats stats)
00268             {
00269                 if (stats.value.strength != 0)
00270                 {
00271                     messages.Add($"{recipient} {(stats.value.strength > 0 ? "increased" : "decreased")} strength by {Math.Abs(stats.value.strength)}");
00272                 }
00273                 if (stats.value.vitality != 0)
00274                 {
00275                     messages.Add($"{recipient} {(stats.value.vitality > 0 ? "increased" : "decreased")} vitality by {Math.Abs(stats.value.vitality)}");
00276                 }
00277                 if (stats.value.dexterity != 0)
00278                 {
00279                     messages.Add($"{recipient} {(stats.value.dexterity > 0 ? "increased" : "decreased")} dexterity by {Math.Abs(stats.value.dexterity)}");
00280                 }
00281                 if (stats.value.luck != 0)
00282                 {
00283                     messages.Add($"{recipient} {(stats.value.luck > 0 ? "increased" : "decreased")} luck by {Math.Abs(stats.value.luck)}");
00284                 }
00285             }
00286         }
00287 
00288         return messages;
00289     }
