00386     public static List<(string recipient, int damage)> GetDamageOutcomes(Effect[] outcomes, bool isUser)
00387     {
00388         List<(string recipient, int damage)> damageOutcomes = new List<(string recipient, int damage)>();
00389 
00390         foreach (var outcome in outcomes)
00391         {
00392             if (outcome.affect is Affect.Damage damage)
00393             {
00394                 string target = outcome.target is Target.Player ? "you" : "the opponent";
00395                 string recipient = isUser ? target : (target == "you" ? "the opponent" : "you");
00396                 damageOutcomes.Add((recipient, damage.value.power));
00397             }
00398         }
00399 
00400         return damageOutcomes;
00401     }
