00271     public void PlayMusic(AudioEffects.Music clip, AudioEffects.Music loopAudio = AudioEffects.Music.NONE)
00272     {
00273         StopMusic();
00274         m_CurrentClip = clip;
00275         if (clip == AudioEffects.Music.NONE) { return; }
00276         m_MusicClips[(int)clip].Source.Play();
00277         m_WaiterCoroutine = StartCoroutine(WaitAndPlayAudio(loopAudio));
00278     }
