00334     private IEnumerator WaitAndPlayAudio(AudioEffects.Music nextClip)
00335     {
00336         yield return new WaitUntil(() => !m_MusicClips[(int)m_CurrentClip].Source.isPlaying);
00337         m_CurrentClip = nextClip;
00338 
00339         if (nextClip == AudioEffects.Music.NONE)
00340         {
00341             yield break;
00342         }
00343 
00344         m_MusicClips[(int)nextClip].Source.Play();
00345         yield return null;
00346     }
