00048             public TextureFunctionInput(Texture texture, string name, int startIndex, int length)
00049             {
00050                 this.Texture = texture;
00051                 this.Name = name;
00052                 this.StartIndex = startIndex;
00053                 this.Length = length;
00054 
00055                 var allSprites = Resources.LoadAll<Sprite>(name).ToList();
00056 
00057                 if (startIndex >= 0 && startIndex + length <= allSprites.Count)
00058                 {
00059                     Sprites = allSprites.GetRange(startIndex, length);
00060                 }
00061                 else
00062                 {
00063                     Debug.LogWarning("Requested sprite range is out of bounds. Assigning an empty list.");
00064                     Sprites = new List<Sprite>(); // Assign an empty list if out of bounds
00065                 }
00066             }
