00073             public TextureFunctionInput(List<Sprite> sprites, int length)
00074             {
00075                 this.Texture = null;
00076                 try
00077                 {
00078                     this.Texture = sprites[0].texture;
00079                 }
00080                 catch (Exception ex)
00081                 {
00082                     Debug.LogError($"{sprites.Count} and here is the last {sprites[sprites.Count - 1].name}");
00083                 }
00084 
00085                 this.Name = "";
00086                 this.StartIndex = 0;
00087                 this.Length = length;
00088 
00089                 Sprites = sprites;
00090             }
