00359         private void Update()
00360         {
00361             if (_paused)
00362             { return; }
00363 
00364             // Check if there's an animation in the queue
00365             if (QueueOfPlayOverAnims.Count > 0)
00366             {
00367                 var (textureInputs, drawOver, callback) = QueueOfPlayOverAnims.Peek();
00368 
00369                 if (drawOver && textureInputs.Count == 1 && textureInputs[0].Sprites.Count > 0)
00370                 {
00371                     // Animate the overlay image with the sprites in textureInputs[0]
00372                     _timeSinceLastFrame += Time.deltaTime;
00373 
00374                     if (_timeSinceLastFrame >= Speed)
00375                     {
00376                         // Update the overlay image with the current frame
00377                         EffectOverlayImage.sprite = textureInputs[0].Sprites[currentFrameDrawover % textureInputs[0].Sprites.Count];
00378                         EffectOverlayImage.enabled = true;
00379 
00380                         // Move to the next frame
00381                         currentFrameDrawover++;
00382                         CheckForCallback(currentFrameDrawover, callback);
00383                         _timeSinceLastFrame = 0f;
00384 
00385                         // Check if we reached the end of the animation
00386                         if (currentFrameDrawover >= textureInputs[0].Sprites.Count)
00387                         {
00388                             // Reset overlay and dequeue animation after it finishes
00389                             QueueOfPlayOverAnims.Dequeue();
00390                             currentFrameDrawover = 0;
00391                             EffectOverlayImage.enabled = false; // Hide overlay after animation
00392                         }
00393                     }
00394                     return; // Skip idle animation while drawing overlay
00395                 }
00396                 else if (!drawOver)
00397                 {
00398                     // Stop idle animation and play queued animation on main images
00399                     _timeSinceLastFrame += Time.deltaTime;
00400 
00401                     if (_timeSinceLastFrame >= Speed)
00402                     {
00403                         for (int i = 0; i < textureInputs.Count; i++)
00404                         {
00405                             var imageComponent = ImageObjects[i].GetComponent<Image>();
00406                             imageComponent.sprite = textureInputs[i].Sprites[currentFrameDrawover % textureInputs[i].Sprites.Count];
00407                             imageComponent.SetAllDirty();
00408                         }
00409 
00410                         currentFrameDrawover++;
00411                         CheckForCallback(currentFrameDrawover, callback);
00412                         _timeSinceLastFrame = 0f;
00413 
00414                         // Check if the main animation is over
00415                         if (currentFrameDrawover >= textureInputs[0].Sprites.Count)
00416                         {
00417                             QueueOfPlayOverAnims.Dequeue(); // Remove current animation
00418                             currentFrameDrawover = 0; // Reset to start idle animation
00419                             EffectOverlayImage.enabled = false; // Hide overlay if it was active
00420                         }
00421                     }
00422                     return; // Skip idle animation while playing queue animation
00423                 }
00424             }
00425             else
00426             {
00427                 EffectOverlayImage.enabled = false; // Ensure overlay is hidden if no queue item
00428             }
00429 
00430             // Regular idle animation if no overlay/queue item
00431             if (IdleTextureFunctionInput.Count == 0)
00432                 return;
00433 
00434             _timeSinceLastFrame += Time.deltaTime;
00435             if (_timeSinceLastFrame >= Speed)
00436             {
00437                 for (int i = 0; i < IdleTextureFunctionInput.Count; i++)
00438                 {
00439                     var textureInput = IdleTextureFunctionInput[i];
00440                     if (textureInput.Sprites.Count == 0)
00441                         continue;
00442 
00443                     var imageComponent = ImageObjects[i].GetComponent<Image>();
00444                     imageComponent.sprite = textureInput.Sprites[_currentFrame % textureInput.Sprites.Count];
00445                     imageComponent.SetAllDirty();
00446                 }
00447                 _currentFrame++;
00448                 _timeSinceLastFrame = 0f;
00449             }
