00451             void CheckForCallback(int frameNum, List<TweenCallbackSystem> callbackSystem)
00452             {
00453                 if (callbackSystem == null)
00454                     return;
00455 
00456                 if (callbackSystem.Count == 0)
00457                     return;
00458 
00459                 if (frameNum != callbackSystem[0].frameNum)
00460                     return;
00461 
00462                 callbackSystem[0].functionCallback();
00463                 callbackSystem.RemoveAt(0);
00464             }
00465         }
