00071         //when the user is invited to a game we sub to this event, this is used in the notification system or the lobby view
00072         public static void AddChallengeInvite(PvPChallengeInvite invite)
00073         {
00074             var key = invite.id.Hex();
00075 
00076             if (!_allChallengeInvite.ContainsKey(key))
00077             {
00078                 _allChallengeInvite.Add(key, invite);
00079                 OnChallengeInviteAddedOrRemoved?.Invoke(key, invite);
00080             }
00081         }
