00110         // Map that goes from combat id to the game status
00111         // sub to this to get the current state of the game
00112         public static void UpdateGameStatus(string key, GameStatus status)
00113         {
00114             if (_gameStatusDict.ContainsKey(key))
00115             {
00116                 if (_gameStatusDict[key] == status)
00117                 {
00118                     return;
00119                 }
00120 
00121                 _gameStatusDict[key] = status;
00122                 OnGameStatusUpdatedOrAdd?.Invoke(key, status);
00123             }
00124             else
00125             {
00126                 _gameStatusDict.Add(key, status);
00127                 OnGameStatusUpdatedOrAdd?.Invoke(key, status);
00128             }
00129         }
