00140         public static async Task<GameStatus> GetGameStatus(FieldElement gameId)
00141         {
00142             var currentStatus = GameStatus.Error;
00143 
00144             var gameStateData = await GetCurrentGameData(gameId);
00145 
00146             if (gameStateData.challengeInvite == null)
00147             {
00148                 //there should always be a challenge invite, if there is non most likely things didnt work out
00149                 return GameStatus.Error;
00150             }
00151             if (!gameStateData.challengeInvite.open)
00152             {
00153                 //someone exited the game so no point in continuing either the sender revoked the invite or the receiver rejected it
00154                 return GameStatus.Exited;
00155             }
00156             currentStatus = GameStatus.Invite;
00157 
00158             if (gameStateData.challengeResponse != null)
00159             {
00160                 //if there is a challenge response we check the state
00161                 if (gameStateData.challengeResponse.open)
00162                 {
00163                     currentStatus = GameStatus.Lobby;
00164                 }
00165                 else
00166                 {
00167                     //again if not open then someone exited the game
00168                     return GameStatus.Exited;
00169                 }
00170             }
00171             if (gameStateData.combatState != null)
00172             {
00173                 if (gameStateData.combatState.phase is Phase.Ended)
00174                 {
00175                     return GameStatus.Ended;
00176                 }
00177 
00178                 currentStatus = GameStatus.Game;
00179             }
00180 
00181             return currentStatus;
00182         }
