00222         public static async Task<GameObject> GetGameDataObject(FieldElement gameId)
00223         {
00224             Dojo.Torii.Query queryForGameInv = QueryHelper.CreateMemberClauseQuery(
00225                ModelPvpInvite, MemberId, operator_,
00226                new Primitive { U128 = StarknetUtils.StarknetHashOperationsUtils.FieldElementToSpan(gameId) }
00227                , 1000, 0);
00228 
00229             var outputQueryChallengeInvite = await DojoEntitiesStorage.worldManager.StandardEntityFetch(queryForGameInv);
00230 
00231             if (outputQueryChallengeInvite.Count == 0)
00232             {
00233                 return null; //return error
00234             }
00235 
00236             var firstEntity = outputQueryChallengeInvite[0];
00237             var entityObj = DojoEntitiesStorage.worldManager.synchronizationMaster.SpawnEntity(firstEntity.HashedKeys, firstEntity.Models.Values.ToArray());
00238 
00239             return entityObj;
