00273         public static async Task<QueryCompiledData> GetGameDataFromQuery(List<ModelInstance> models)
00274         {
00275             PvPChallengeInvite challengeInvite = null;
00276             PvPChallengeResponse challengeResponse = null;
00277             CombatState combatState = null;
00278             PvPCombatants pvPCombatants = null;
00279 
00280             foreach (var model in models)
00281             {
00282                 switch (model)
00283                 {
00284                     case PvPChallengeInvite pvpChallengeInvite:
00285                         challengeInvite = pvpChallengeInvite;
00286                         break;
00287 
00288                     case PvPChallengeResponse pvpChallengeResponse:
00289                         challengeResponse = pvpChallengeResponse;
00290                         break;
00291 
00292                     case CombatState combatStateModel:
00293                         await combatStateModel.ManuallyUpdateEnum();  //again issue like always, the SDK doesnt like enums or structs so we have to manually update them
00294                         combatState = combatStateModel;
00295                         break;
00296 
00297                     case PvPCombatants combatants:
00298                         pvPCombatants = combatants;
00299                         break;
00300 
00301                     default:
00302                         break;
00303                 }
00304             }
00305 
00306             return new QueryCompiledData { challengeInvite = challengeInvite, challengeResponse = challengeResponse, combatState = combatState, pvPCombatants = pvPCombatants };
00307         }
