00396                                 }}
00397                                 ... on blob_arena_PvPCombatants{{
00398                                   id
00399                                   combatants{{
00400                                     _0
00401                                     _1
00402                                   }}
00403                                 }}
00404                               }}
00405                             }}
00406                           }}
00407                         }}
00408                       }}
00409                     }}";
00410 
00411             var client = new GraphQLClient($"{DojoEntitiesStorage.worldManagerData.toriiUrl}/graphql");
00412 
