00414             {
00415                 Query = queryString,
00416             };
00417 
00418             var response = await client.Send<dynamic>(request);
00419 
00420             var jsonString = Newtonsoft.Json.JsonConvert.SerializeObject(response, Newtonsoft.Json.Formatting.Indented);
00421             JObject jsonObj = JObject.Parse(jsonString);
00422 
00423             var edges = jsonObj["Data"]["blobArenaPvPChallengeInviteModels"]["edges"];
00424 
00425             QueryCompiledData resultManualQuery = new QueryCompiledData();
00426 
00427             foreach (var edge in edges)
00428             {
00429 #if UNITY_EDITOR
00430                 var objectName = StarknetUtils.StarknetHashOperationsUtils.PoseidonHash(new FieldElement[] { gameId }).Hex();
00431 #else
00432                 var objectName = new FieldElement(JavaScriptUtils.PoseidonHashHelper(new string[1] { gameId.Hex() })).Hex();
00433 #endif
00434 
00435                 GameObject existingGameObject = GameObject.Find(objectName);
00436 
00437                 GameObject newGameObject;
00438 
00439                 if (existingGameObject == null)
00440                 {
00441                     // Create a new GameObject if it doesn't exist
00442                     newGameObject = new GameObject(objectName);
00443 
00444                     // Find GameManager and set the new GameObject as a child of GameManager
00445                     GameObject gameManager = GameObject.Find("WorldManager");
00446                     if (gameManager != null)
00447                     {
00448                         newGameObject.transform.SetParent(gameManager.transform);
00449                     }
00450                     else
00451                     {
00452                         Debug.LogWarning("GameManager not found, skipping GameObject creation");
00453                         continue;  // Skip to the next iteration if GameManager doesn't exist
00454                     }
00455                 }
00456                 else
00457                 {
00458                     newGameObject = existingGameObject;
00459                 }
00460 
00461                 PvPChallengeInvite invite = null;
00462                 PvPChallengeResponse responseModel = null;
00463                 CombatState combatState = null;
00464                 PvPCombatants combatantsModel = null;
