00466                 var models = edge["node"]["entity"]["models"];
00467 
00468                 foreach (var model in models)
00469                 {
00470                     var typename = (string)model["__typename"];
00471 
00472                     if (typename == "blob_arena_PvPChallengeInvite")
00473                     {
00474                         var inviteComponent = newGameObject.GetComponent<PvPChallengeInvite>();
00475                         if (inviteComponent == null)
00476                         {
00477                             inviteComponent = newGameObject.AddComponent<PvPChallengeInvite>();
00478                         }
00479 
00480                         inviteComponent.id = new FieldElement((string)model["id"]);
00481                         inviteComponent.sender = new FieldElement((string)model["sender"]);
00482                         inviteComponent.receiver = new FieldElement((string)model["receiver"]);
00483                         inviteComponent.collection_address = new FieldElement((string)model["collection_address"]);
00484                         inviteComponent.combatant = new FieldElement((string)model["combatant"]);
00485                         inviteComponent.phase_time = Convert.ToUInt64((string)model["phase_time"], 16);
00486                         inviteComponent.open = (bool)model["open"];
