00540 
00541                         combatantsModel = combatantsComponent;
00542                     }
00543                 }
00544 
00545                 if (invite == null)
00546                 {
00547                     Debug.LogError($"Failed to find the invite for game {gameId.Hex()}, destroying GameObject.");
00548                     GameObject.Destroy(newGameObject);
00549                 }
00550                 else
00551                 {
00552                     resultManualQuery = new QueryCompiledData(invite, responseModel, combatState, combatantsModel);
