00275     private async void SetStateOfObjects()
00276     {
00277         foreach (var game in _gameIdToObjectElementMap)
00278         {
00279             var state = GamesDataModel._gameStatusDict.TryGetValue(game.Key, out var cachedState)
00280                 ? cachedState
00281                 : await GamesDataModel.GetGameStatus(new FieldElement(game.Key));
00282 
00283             if (state == GamesDataModel.GameStatus.Invite)
00284             {
00285                 if (PvPChallengeInvite.IsAdminOfGame(GamesDataModel._allChallengeInvite[new FieldElement(game.Key).Hex()], LocalDataModel.CurrentAccount.Address))
00286                 {
00287                     game.Value.SetActive((sorting & 1) == 0);
00288                 }
00289                 else
00290                 {
00291                     game.Value.SetActive((sorting & 2) == 0);
00292                 }
00293             }
00294             else if (state == GamesDataModel.GameStatus.Lobby)
00295             {
00296                 game.Value.SetActive((sorting & 4) == 0);
00297             }
00298             else if (state == GamesDataModel.GameStatus.Game)
00299             {
00300                 game.Value.SetActive((sorting & 8) == 0);
00301             }
00302         }
00303     }
