00276     private async void SetStateOfObjects()
00277     {
00278         foreach (var game in _gameIdToObjectElementMap)
00279         {
00280             var state = GamesDataModel._gameStatusDict.TryGetValue(game.Key, out var cachedState)
00281                 ? cachedState
00282                 : await GamesDataModel.GetGameStatus(new FieldElement(game.Key));
00283 
00284             if (state == GamesDataModel.GameStatus.Invite)
00285             {
00286                 if (PvPChallengeInvite.IsAdminOfGame(GamesDataModel._allChallengeInvite[new FieldElement(game.Key).Hex()], LocalDataModel.CurrentAccount.Address))
00287                 {
00288                     game.Value.SetActive((sorting & 1) == 0);
00289                 }
00290                 else
00291                 {
00292                     game.Value.SetActive((sorting & 2) == 0);
00293                 }
00294             }
00295             else if (state == GamesDataModel.GameStatus.Lobby)
00296             {
00297                 game.Value.SetActive((sorting & 4) == 0);
00298             }
00299             else if (state == GamesDataModel.GameStatus.Game)
00300             {
00301                 game.Value.SetActive((sorting & 8) == 0);
00302             }
00303         }
00304     }
