00314         public static FieldElement FetchSavedBlob()
00315         {
00316             var blob = PlayerPrefs.GetString($"SavedBlob-{CurrentAccount.Address.Hex()}-{CurrentGameEvent}");
00317 
00318             if (blob == "")
00319             {
00320                 return null;
00321             }
00322 
00323             return new FieldElement(blob);
00324         }
00325 
00326         public static void SaveBlob(FieldElement blob)
00327         {
00328             PlayerPrefs.SetString($"SavedBlob-{CurrentAccount.Address.Hex()}-{CurrentGameEvent}", blob.Hex());
00329             PlayerPrefs.Save();
00330         }
00331 
00332         public static bool LoadLocalBlob()
00333         {
00334             if (PlayerDataModel.selectedPlayableCharacters.Count != 0)
00335             {
00336                 var savedBlob = FetchSavedBlob();
00337 
00338                 if (savedBlob != null)
00339                 {
00340                     if (WorldDataModel.AllPlayableCharactersInTheGame.ContainsKey(savedBlob.Hex()))
00341                     {
00342                         ChoosenBlobert = WorldDataModel.AllPlayableCharactersInTheGame[savedBlob.Hex()].GetTokenId()
00343                             .Hex();
00344                         return true;
00345                     }
00346                 }
00347 
00348                 ChoosenBlobert = PlayerDataModel.selectedPlayableCharacters[0].GetTokenId().Hex();
00349                 SaveBlob(PlayerDataModel.selectedPlayableCharacters[0].GetTokenId());
00350                 return true;
00351             }
00352 
00353             return false;
00354         }
