00417         public static DojoData FetchGameData(FieldElement gameId, FieldElement accountAddress, int roundNum)
00418         {
00419             string key = $"{gameId.Hex()}-{accountAddress.Hex()}-{roundNum}";
00420 
00421             string serializedData = JavaScriptUtils.GetItem(key);
00422 
00423             if (!string.IsNullOrEmpty(serializedData))
00424             {
00425                 DojoDataSerializable serializableData = JsonUtility.FromJson<DojoDataSerializable>(serializedData);
00426                 Debug.Log($"Data found for this game {gameId.Hex()}: " + serializedData);
00427                 return serializableData.ToDojoData();
00428             }
00429             else
00430             {
00431                 Debug.LogWarning(
00432                     "Nothing was found; this could be because this is the first time this game was played");
00433             }
00434 
00435             return null;
00436         }
