00455         public static Attack[] FetchBlobertAttacks(FieldElement blobId, FieldElement accountAddress)
00456         {
00457             string key = $"{accountAddress.Hex()}-{blobId.Hex()}";
00458             List<Attack> attacksSet = new();
00459 
00460             string serializedData = JavaScriptUtils.GetItem(key);
00461 
00462             if (!string.IsNullOrEmpty(serializedData))
00463             {
00464                 var attackIds = serializedData.Split('-');
00465 
00466                 foreach (var attackId in attackIds)
00467                 {
00468                     if (WorldDataModel.AllAttacksDict.ContainsKey(attackId))
00469                     {
00470                         attacksSet.Add(WorldDataModel.AllAttacksDict[attackId]);
00471                     }
00472                 }
00473             }
00474 
00475             return attacksSet.ToArray();
00476         }
