00497         // Function that adds an attack to the blobert's list and saves it
00498         public static void AddAttackToBlobert(FieldElement blobId, FieldElement accountAddress, Attack attack)
00499         {
00500             string key = $"{accountAddress.Hex()}-{blobId.Hex()}";
00501 
00502             string serializedData = JavaScriptUtils.GetItem(key);
00503             HashSet<string> attackIdsSet = new();
00504 
00505             if (!string.IsNullOrEmpty(serializedData))
00506             {
00507                 var attackIds = serializedData.Split('-');
00508                 attackIdsSet = new HashSet<string>(attackIds);
00509             }
00510 
00511             string attackIdHex = attack.id.Hex();
00512 
00513             if (!attackIdsSet.Contains(attackIdHex))
00514             {
00515                 attackIdsSet.Add(attackIdHex);
00516 
00517                 string newSerializedData = string.Join("-", attackIdsSet);
00518                 JavaScriptUtils.SetItem(key, newSerializedData);
00519 
00520                 OnBlobertAttackChanged?.Invoke(blobId, attack, true);
00521             }
00522         }
00523 
00524         // Function that removes an attack from the blobert's list and saves it
