00526         {
00527             string key = $"{accountAddress.Hex()}-{blobId.Hex()}";
00528 
00529             string serializedData = JavaScriptUtils.GetItem(key);
00530             HashSet<string> attackIdsSet = new();
00531 
00532             if (!string.IsNullOrEmpty(serializedData))
00533             {
00534                 var attackIds = serializedData.Split('-');
00535                 attackIdsSet = new HashSet<string>(attackIds);
