00315 
00316         LeanTween.alphaCanvas(_backgroundPre.gameObject.GetComponent<CanvasGroup>(), 1.0f, 0.3f)
00317             .setOnComplete(() =>
00318                 {
00319                     // Fade in the canvas group to opacity 1 over 3 seconds
00320                     LeanTween.alphaCanvas(_preLoginCanvasGroup, 1, 1f).setOnComplete(() =>
00321                     {
00322                         m_Typewriter.SetTextToType("YOU CHOSE VIOLENCE", () => m_Typewriter.StartWaitingTextAnimation());
00323                         // Wait for 3 seconds after full opacity
00324                         LeanTween.delayedCall(5f, () =>
00325                         {
00326                             // Fade out the canvas group to 0 over 3 seconds
00327                             LeanTween.alphaCanvas(_preLoginCanvasGroup, 1, 2.0f)
00328                                 .setDelay(1.0f)
00329                                 .setOnComplete(() =>
00330                                 {
00331                                     m_Typewriter.ClearText();
00332                                     // Move the _backgroundPost image into view with a bounce effect
00333                                     LeanTween.move(_backgroundPost.rectTransform, _backgroundPre.rectTransform.position, 1f)
00334                                         .setEase(LeanTweenType.easeOutElastic)
00335                                         .setOnComplete(() =>
