00124     private void Update()
00125     {
00126         // Check if a notification is displayed and update the timer
00127         if (_notificationObject.activeSelf)
00128         {
00129             _notificationTimer -= Time.deltaTime;
00130 
00131             // If the timer expires, destroy the current notification
00132             if (_notificationTimer <= 0f)
00133             {
00134                 DeleteCurrentNotification();
00135             }
00136         }
00137 
00138         // If there's no current notification and there are notifications in the queue, display the next one
00139         if (!_notificationObject.activeSelf && _notificationQueue.Count > 0)
00140         {
00141             ShowNextNotification();
00142         }
00143     }
