00278     // Utility function to show the next notification
00279     private void ShowNextNotification()
00280     {
00281         var notificationData = _notificationQueue.First?.Value;
00282 
00283         if (notificationData == null)
00284         {
00285             return;
00286         }
00287 
00288         _notificationObject.SetActive(true);
00289 
00290         foreach (var button in notificationData.Value.ButtonTypes)
00291         {
00292             var buttonPrefab = Instantiate(ButtonPrefabs[(int)button.ButtonType], _buttonParent.transform);
00293 
00294             var baseButton = buttonPrefab.GetComponent<BaseButton>();
00295             baseButton.GetText().text = button.Message;
00296             baseButton.onClickCalls = button.Callbacks;
00297         }
00298 
00299         _notiText.text = notificationData.Value.Message;
00300 
00301         _notibackground.sprite = BackgroundColours[(int)notificationData.Value.ColourOfBackground];
00302 
00303         if (notificationData.Value.BlobId != null)
00304         {
00305             _notiCharAnimator.gameObject.SetActive(true);
00306 
00307             var blobData = WorldDataModel.AllPlayableCharactersInTheGame[notificationData.Value.BlobId.Hex()];
00308             if (blobData.GetNftType() == BlobertTypes.NFTTypes.AMMABlobs)
00309             {
00310                 _notiCharAnimator.InitilizeComponentQuick(WorldDataModel.AllPlayableCharactersInTheGame[notificationData.Value.BlobId.Hex()], AmmaAnimationController.QuickIdleNoMaskR);
00311             }
00312             else
00313             {
00314                 _notiCharAnimator.InitilizeComponentQuick(WorldDataModel.AllPlayableCharactersInTheGame[notificationData.Value.BlobId.Hex()], BlobertAnimationController.IdleDefaultFrontR);
00315             }
00316         }
00317         else
00318         {
00319             _notiCharAnimator.gameObject.SetActive(false);
00320         }
00321 
00322         _notificationTimer = _timerPerNotification;
00323     }
