00327     // Utility function to delete the current notification
00328     public void DeleteCurrentNotification()
00329     {
00330         if (_notificationObject.activeSelf)
00331         {
00332             //delete all of the buttion
00333             foreach (Transform child in _buttonParent.transform)
00334             {
00335                 Destroy(child.gameObject);
00336             }
00337             _notificationObject.SetActive(false);
00338 
00339             //clear the object from the linked list
00340 
00341             if (_notificationQueue.Count > 0)
00342             {
00343                 _notificationQueue.RemoveFirst();
00344             }
00345         }
00346     }
