00021     public enum FunctionNames
00022     {
00023         SendInvite,  // admin sends the invite
00024         RevokeInvite, // admin revokes invte
00025 
00026         RespondInvite, //guest confirms
00027         RevokeResponse,// admin leaves
00028         RejectInvite, //guest rejects invit
00029         RejectResponse, //guest rejects response
00030         AcceptResponse,
00031 
00032         CommitAttack,
00033         RevealAttack,
00034         RunRound,
