00040     public static string EnumToString(this FunctionNames functionName)
00041     {
00042         switch (functionName)
00043         {
00044             case FunctionNames.SendInvite:
00045                 return "send_invite";
00046 
00047             case FunctionNames.RevokeInvite:
00048                 return "rescind_invite";
00049 
00050             case FunctionNames.RespondInvite:
00051                 return "respond_invite";
00052 
00053             case FunctionNames.RevokeResponse:
00054                 return "rescind_response";
00055 
00056             case FunctionNames.RejectInvite:
00057                 return "reject_invite";
00058 
00059             case FunctionNames.RejectResponse:
00060                 return "reject_response";
00061 
00062             case FunctionNames.AcceptResponse:
00063                 return "accept_response";
00064 
00065             case FunctionNames.CommitAttack:
00066                 return "commit_attack";
00067 
00068             case FunctionNames.RevealAttack:
00069                 return "reveal_attack";
00070 
00071             case FunctionNames.RunRound:
00072                 return "run_round";
00073 
00074             case FunctionNames.Forfeit:
00075                 return "forfeit";
00076 
00077             case FunctionNames.KickInactivePlayer:
00078                 return "kick_inactive_player";
00079 
00080             case FunctionNames.DojoInit:
00081                 return "dojo_init";
00082 
00083             default:
00084                 return "";
00085         }
00086     }
