00098 
00099         public SendInviteStruct(FieldElement receiver, FieldElement collectionAddress, FieldElement tokenId, List<AttackStruct> attacks, UInt64 phaseTime, EndpointDojoCallStruct endpointData)
00100         {
00101             this.receiver = receiver;
00102             this.collectionAddress = collectionAddress;
00103 
00104             var split = StarknetUtils.StarknetHashOperationsUtils.SplitU256ToU128(tokenId.Hex());
00105             this.tokenId = split.low;
00106             this.emptyFieldElement = split.high;
00107 
00108             this.elements = attacks.Count;
00109             this.phaseTime = phaseTime;
00110             this.attacks = attacks;
00111             this.endpointData = endpointData;
00112         }
