00054         public async Task<int> SynchronizeEntities()
00055         {
00056 #if UNITY_WEBGL && !UNITY_EDITOR
00057             var entities = await worldManager.wasmClient.Entities(worldManager.dojoConfig.query);
00058 #else
00059             var entities = await Task.Run(() => worldManager.toriiClient.Entities(worldManager.dojoConfig.query));
00060 #endif
00061 
00062             var entityGameObjects = new List<GameObject>();
00063             foreach (var entity in entities)
00064             {
00065                 entityGameObjects.Add(SpawnEntity(entity.HashedKeys, entity.Models.Values.ToArray()));
00066             }
00067 
00068             OnSynchronized?.Invoke(entityGameObjects);
00069             return entities.Count;
00070         }
