00072         // Spawn an entity in the world if its not there
00073         public GameObject SpawnEntity(FieldElement hashedKeys, Model[] entityModels)
00074         {
00075             var ent = worldManager.Entity(hashedKeys.Hex());
00076 
00077             if (ent != null)
00078             {
00079                 HandleEntityUpdate(hashedKeys, entityModels);
00080                 return ent.gameObject;
00081             }
00082 
00083             var entityGameObject = worldManager.AddEntity(hashedKeys.Hex());
00084 
00085             // Initialize each one of the entity models
00086             foreach (var entityModel in entityModels)
00087             {
00088                 string[] parts = entityModel.Name.Split('-');
00089                 string name = parts[1];
00090 
00091                 // Check if we have a model definition for this entity model
00092                 var model = models.FirstOrDefault(m => m.GetType().Name == name);
00093                 if (model == null)
00094                 {
00095                     //Debug.LogWarning($"Model {entityModel.Name} not found");
00096                     continue;
00097                 }
00098 
00099                 // Add the model component to the entity
00100                 var component = (ModelInstance)entityGameObject.AddComponent(model.GetType());
00101                 component.Initialize(entityModel);
00102             }
00103 
00104             OnEntitySpawned?.Invoke(entityGameObject);
00105             return entityGameObject;
00106         }
