00108         // Called when there is an entity being updated on the torii/chain side
00109         private void HandleEntityUpdate(FieldElement hashedKeys, Model[] entityModels)
00110         {
00111             var entity = GameObject.Find(hashedKeys.Hex()); //get the entity by name
00112 
00113             if (entity == null && CreateNewWhenUpdated)   //create a new entity if it doesn't exist
00114             {
00115                 entity = SpawnEntity(hashedKeys, entityModels);
00116                 return;
00117             }
00118 
00119             // Update each one of the entity models scripts
00120             foreach (var entityModel in entityModels)
00121             {
00122                 string[] parts = entityModel.Name.Split('-');
00123                 string @namespace = parts[0];
00124                 string name = parts[1];
00125 
00126                 var component = entity.GetComponent(name);
00127 
00128                 if (component == null)
00129                 {
00130                     // TODO: decouple?
00131                     var model = models.FirstOrDefault(m => m.GetType().Name == name);
00132                     if (model == null)
00133                     {
00134                         Debug.LogWarning($"Model {entityModel.Name} not found");
00135                         continue;
00136                     }
00137 
00138                     component = (ModelInstance)entity.AddComponent(model.GetType());
00139                 }
00140 
00141                 // update component with new model data
00142                 ((ModelInstance)component).OnUpdate(entityModel);
00143             }
00144         }
