00168         public GameObject SpawnEntityCustom(FieldElement hashedKeys, Model[] entityModels)
00169         {
00170             // Check if there are any valid models to spawn before creating the entity
00171             bool hasValidModel = entityModels.Any(entityModel => nameOfModelsToUpdate.Contains(entityModel.Name.Split('-')[1]));
00172             if (!hasValidModel)
00173             {
00174                 Debug.LogWarning("No valid models found to spawn the entity.");
00175                 return null;
00176             }
00177 
00178             // Add the entity to the world.
00179             var entityGameObject = worldManager.AddEntity(hashedKeys.Hex());
00180 
00181             foreach (var entityModel in entityModels)
00182             {
00183                 string name = entityModel.Name.Split('-')[1];
00184 
00185                 // Only spawn if the name is in the hashset
00186                 if (!nameOfModelsToUpdate.Contains(name))
00187                     continue;
00188 
00189                 // Check if we have a model definition for this entity model
00190                 var model = models.FirstOrDefault(m => m.GetType().Name == name);
00191 
00192                 if (model == null)
00193                 {
00194                     Debug.LogWarning($"Model {entityModel.Name} not found");
00195                     continue;
00196                 }
00197 
00198                 // Add the model component to the entity
00199                 var component = (ModelInstance)entityGameObject.AddComponent(model.GetType());
00200                 component.Initialize(entityModel);
00201             }
00202 
00203             OnEntitySpawned?.Invoke(entityGameObject);
00204             return entityGameObject;
00205         }
