00212         private void HandleEntityUpdateCustom(FieldElement hashedKeys, Model[] entityModels)
00213         {
00214             foreach (var entityModel in entityModels)
00215             {
00216                 Debug.Log(entityModel.Name);
00217                 //if the name is in this format blob_arena-PvPChallengeInvite then get the last section
00218                 var nameOfClass = entityModel.Name.Split('-')[1];
00219 
00220                 //if the name is in the hashset then update the entity
00221                 if (!nameOfModelsToUpdate.Contains(nameOfClass))
00222                 {
00223                     continue;
00224                 }
00225 
00226                 var entity = GameObject.Find(hashedKeys.Hex()); //get the entity by name
00227 
00228                 if (entity == null && CreateNewWhenUpdated)   //create a new entity if it doesn't exist
00229                 {
00230                     entity = SpawnEntity(hashedKeys, entityModels);
00231                     return;
00232                 }
00233 
00234                 var component = entity.GetComponent(nameOfClass);
00235 
00236                 if (component == null)
00237                 {
00238                     var model = models.FirstOrDefault(m => m.GetType().Name == nameOfClass);
00239                     if (model == null)
00240                     {
00241                         Debug.LogWarning($"Model {entityModel.Name} not found");
00242                         continue;
00243                     }
00244 
00245                     component = (ModelInstance)entity.AddComponent(model.GetType());
00246                 }
00247 
