00288         private ModelInstance[] LoadModels()
00289         {
00290             List<ModelInstance> models = new();
00291 
00292             Assembly[] assemblies = AppDomain.CurrentDomain.GetAssemblies();
00293 
00294             foreach (Assembly assembly in assemblies)
00295             {
00296                 var modelTypes = assembly.GetTypes()
00297                     .Where(t => typeof(ModelInstance).IsAssignableFrom(t) && !t.IsAbstract);
00298 
00299                 foreach (Type modelType in modelTypes)
00300                 {
00301                     models.Add((ModelInstance)gameObject.AddComponent(modelType));
00302                 }
00303             }
00304 
00305             return models.ToArray();
00306         }
