00312         public List<ModelInstance> GetScriptsFromQueryResult(Model[] entityModels)
00313         {
00314             var scripts = new List<ModelInstance>();
00315             foreach (var entityModel in entityModels)
00316             {
00317                 string[] parts = entityModel.Name.Split('-');
00318                 string @namespace = parts[0];
00319                 string name = parts[1];
00320 
00321                 var model = models.FirstOrDefault(m => m.GetType().Name == name);
00322 
00323                 if (model == null)
00324                 {
00325                     Debug.LogWarning($"Model {entityModel.Name} not found");
00326                     continue;
00327                 }
00328 
00329                 var scriptInstance = (ModelInstance)Activator.CreateInstance(model.GetType());
00330                 scriptInstance.Initialize(entityModel);
00331 
00332                 scripts.Add(scriptInstance);
00333             }
00334             return scripts;
00335         }
